typedef long long ll;
class Solution {
public:
    long long countExcellentPairs(vector<int>& nums, int k) 
    {
        set<int>Set;
        for(auto &x:nums)Set.insert(x);
        nums.clear();
        for(auto &x:Set)nums.push_back(x);
        int n=nums.size();
        vector<int>cnt(n,0);
        for(int i=0;i<n;i++)
        {
            int cur=0;
            for(int j=0;j<=30;j++)cur+=((nums[i]>>j)&1);
            cnt[i]=cur;
        }
        sort(cnt.begin(),cnt.end());
        ll ans=0;
        for(int i=0;i<n;i++)
        {
            int target=k-cnt[i];
            auto it=lower_bound(cnt.begin(),cnt.end(),target);
            if(it==cnt.end())continue;
            int idx=it-cnt.begin();
            ans+=n-idx;
        }
        return ans;
    }
};
