```
class Solution {
public:
    long long countExcellentPairs(vector<int>& a, int k) {
        /*
            Create a venn diagram for x1 OR x2 and x1 AND x2.
            The sum of count of digits in the above two values is basically the sum of digits of two no.s

            So, we can count the bits in each no.f and store them in a map. the map can have a size of at max
            32 because there can be at max 32 bits in an integer. We can put the pairs in an unrodered_set
            to count the distinct ones and and count pairs like (x1, x1) separately
        */

        unordered_set<int> s(begin(a), end(a));
        int bitCtr[32] = {0};

        long long count = 0;
        for(int i: s) {
            // So, lets say ith element had p bits in it. We need to find p + q >= k, so q >= k - p
            // and all such existing ones

            int p = __builtin_popcount(i);

            if (p + p >= k)
                count++;

            for(int j = max(k-p, 0); j < 32; j++) {
                if (j + p >= k)
                    count += 2LL * bitCtr[j];
            }

            bitCtr[p]++;
        }

        return count;
    }
};
