# Code
```
class Solution {
public:
    void bfs(vector<int> &edges, int src, map<int, int> &nodes_reachable){
        // queue of pair to store node and distance
        // Q[i].first - store the node 
        // Q[i].second - store the distance of that node from starting node
        queue<pair<int, int>> Q;
        // distance of src from src will be zero
        Q.push({src, 0});
        // boolean array to keep track of the visited node
        vector<bool> vis(edges.size(), 0);
        vis[src] = 1;
        while(Q.size()){
            pair<int, int> fr = Q.front();
            Q.pop();
            nodes_reachable[fr.first] = fr.second;
            // since there can be maximum only one outgoing edge from any node, hence we don't need to use any loop
            if(edges[fr.first] != -1 && !vis[edges[fr.first]]){
                vis[edges[fr.first]] = 1;
                Q.push({edges[fr.first], fr.second + 1});
            }
        }
    }

    int closestMeetingNode(vector<int>& edges, int node1, int node2) {

        // map to store the minimum distance of the nodes reachable from node1
        // nodes_reachable_from1[a] = b means a is reachable from node1 and minimum distance to reach a from node1 is b
        // nodes_reachable_from2[a] = b means a is reachable from node2 and minimum distance to reach a from node2 is b

        map<int, int> nodes_reachable_from1; 
        map<int, int> nodes_reachable_from2;

        /*  
            Since weight of every edge is 1, We don't need to think of dijktra algo to find the shortest 
            distance from node1 to all other reachable nodes. Applying BFS will give us the shortest distance.
        */   
        bfs(edges, node1, nodes_reachable_from1);
        bfs(edges, node2, nodes_reachable_from2);

        // ans store the minimum distance of the node reachable from both node1 and node2 
        int ans = numeric_limits<int>::max();

        //Index of the node reachable from both node1 and node2 having minimum distance
        int node_ind = -1;

        /* 
            traverse all the nodes reachable from node1 and check if it is also reachable from node2. If reachable, 
            update the ans if the maximum of {z1(distance of curr_node from node1), z2(distance of curr_node from node2)}
            is less than ans. Also update node_ind to curr_node.
        */

        for(auto it : nodes_reachable_from1){
            // check if nodes reachable from 1 is also reachable from 2
            if(nodes_reachable_from2.find(it.first) != nodes_reachable_from2.end()){
                // update the minimum distance
                int z = min({ans, max({nodes_reachable_from2[it.first], it.second})});
                if(z < ans){
                    ans = z;
                    node_ind = it.first;
                }
            }
        }
        // return the node reachbale from both node1 and node2 and have minimum distance from both
        return node_ind;
    }
};
```

