int closestMeetingNode(vector<int>& edges, int node1, int node2) {
    int n=edges.size();
    // Create two distance vectors to save the distance of each node from node1 and node2
    // -1 represents unvisited node
    vector<int> v1(n,-1),v2(n,-1);
    // fill the distance vector for node1
    // cur represents the current element and d represent the distance travelled
    int cur= node1,d=0;
    while(1)
    {
        // if the current node doesnt have any linked node 
        // or ve have already visited that node and updated the distance.
        if(cur==-1 || v1[cur]!=-1) break;
        // else assign the distance travelled to the current node and hop to the linked node
        v1[cur]=d++;
        cur= edges[cur];
    }
    // fill the distance vector for node1
    cur=node2,d=0;
    // we keep a track of all the nodes that we visited while traversing node2's path and that came in node1's path
    // we do this by checking if the distance vector of tha cur is already filled in v1

    int ans = -1, ansd=INT_MAX;
    while(1)
    {
        if(cur==-1 || v2[cur]!=-1) break;

        v2[cur]=d++;
        // if that node has already occured in node1's path
        if(v1[cur]!=-1) 
            if( max (v1[cur],v2[cur]) <= ansd)
            {
                // check for the questions requirement
                // the max of distance of both node 1 and node 2 to the common node 
                // must be minimized
                // ansd represent max distance of (node1->cur, node2->cur)
                if( max(v1[cur],v2[cur])==ansd) 
                    ans= min(ans,cur);
                else 
                    ans=cur,ansd= max (v1[cur],v2[cur]);
            }
        cur=edges[cur];
    }


    return ans;
}

