    public int closestMeetingNode(int[] edges, int node1, int node2) {
        int n = edges.length;
        int[] n1Distances = calculateDistanceFromNode(node1, edges);
        int[] n2Distances = calculateDistanceFromNode(node2, edges);

        int node = -1;
        int dis = Integer.MAX_VALUE;

        for (int i = 0; i < n; i++) {
