                int curNode = bfs.poll();
                distances[curNode] = dis;
                if (edges[curNode] != -1 && !vis[edges[curNode]]) {
                    vis[edges[curNode]] = true;
                    bfs.offer(edges[curNode]);
                }
            }

            dis++;
        }
