# Intuition
Form a vector that contain distance of source node to current node and check which node is best for given condition.

# Approach
First form two vectors that contain the distance between node1/node2 and all other nodes.
Then iterate over all the values of this vector and check which intermidiate node will give the answer.



# Code
```
class Solution {
public:
    void dfs1(vector<int>&edges , int node , int m , vector<int>&r1)
    {
        if(r1[node] != -1)
        return;

        r1[node] = r1[m]+1;
        if(edges[node] == -1) return;
        dfs1(edges , edges[node] , node , r1);
    }
    void dfs2(vector<int>&edges , int node , int m , vector<int>&r2)
    {
        if(r2[node] != -1)
        return;

        r2[node] = r2[m]+1;
        if(edges[node] == -1) return;
        dfs2(edges , edges[node] , node , r2);
    }
    int closestMeetingNode(vector<int>& edges, int node1, int node2) {
        int n = edges.size();
        vector<int>r1(n , -1) , r2(n , -1);

        dfs1(edges , node1 , node1 , r1);
        dfs2(edges , node2 , node2 , r2);
        int ans = -1 , mx = 1e9;
        for(int i = 0;i<n;i++)
        {
            if(r1[i] == -1 || r2[i] == -1) continue;
            int t = max(r1[i] , r2[i]);
            if(t < mx)
            {
                ans = i;
                mx = t;
            }
        }
        if(ans == 1e9) return -1;
        return ans;
    }
};
```

