class Solution {
public:

    void dfs(int curr,vector<int>& edges,vector<int>& distance,vector<bool>& visited)
    {
        visited[curr] = true;
        int next = edges[curr];
        if(next != -1 && !visited[next])
        {
            distance[next] = distance[curr] + 1;
            dfs(next,edges,distance,visited);
        }
    }
    int closestMeetingNode(vector<int>& edges, int node1, int node2) {
        int n = edges.size();
        int ans = -1;
        int minDist = INT_MAX;

        vector<int> distance1(n,0),distance2(n,0);
        vector<bool> visited1(n,false),visited2(n,false);

        dfs(node1,edges,distance1,visited1);
        dfs(node2,edges,distance2,visited2);

        for(int node=0;node<n;node++)
        {
            if(visited1[node] && visited2[node] && minDist>max(distance1[node],distance2[node]))
            {
                minDist = max(distance1[node],distance2[node]);
                ans = node;
            }
        }
        return ans;
    }
};
