// Approach #1: Using 2 Sets

// Time complexity: O(n)
// Space complexity: O(n)

const closestMeetingNode = function(edges, node1, node2) {
	const neighbours1 = new Set();
	const neighbours2 = new Set();

	let answer = -1;

	while (node1 !== -1 || node2 !== -1) {
		if (node1 !== -1) {		
			if (neighbours1.has(node1)) // cycle
				node1 = -1;
			else
				neighbours1.add(node1);
		}

		if (node2 !== -1) {		
			if (neighbours2.has(node2)) // cycle
				node2 = -1;
			else
				neighbours2.add(node2);
		}

		if (neighbours2.has(node1))
			answer = node1;

		if (neighbours1.has(node2))
			answer = answer === -1 ? node2 : Math.min(answer, node2);

		if (node1 !== -1)
			node1 = edges[node1];

		if (node2 !== -1)     
			node2 = edges[node2];

		if (answer !== -1)
			return answer;
	}

	return answer;
};
