    {
        vector<int>dis(n,INT_MAX);
        dis[node]=0;
        queue<int>q;
        q.push(node);
        int level=0;
        while(q.size())
        {
            int size=q.size();
            for(int i=0;i<size;i++)
            {
                auto temp=q.front();
                dis[temp]=level;
                q.pop();
                for(auto &it:dp[temp])
