```
class Solution {
public:
    int closestMeetingNode(vector<int>& edges, int node1, int node2)
    {
        int size = edges.size();
        vector<int> dist1(size, -1); // will store the distances from node1 to all other nodes
        vector<int> dist2(size, -1);// will store the distances from node2 to all other nodes

        int dist = 0;
		// calculate distance from node1 to other nodes
        while(node1 != -1)
        {
            if(dist1[node1] >= 0)break;   .// stoping condition if there's a loop 
            dist1[node1] = dist;   // updating dist in dist1 vector from node1
            dist++;
            node1 = edges[node1];
        }

        dist = 0;
		// calculate distance from node2 to other nodes
        while(node2 != -1)
        {
            if(dist2[node2] >= 0)break;  // stoping condition if there's a loop 
            dist2[node2] = dist;// updating dist in dist2 vector from node2
            dist++;
            node2 = edges[node2];
        }

        pair<int, int> ans = make_pair(INT_MAX,INT_MAX);    // to keep track of minimum dist and ans node

        for(int i = 0; i < size; i++)
        {
            if(dist1[i] == -1 || dist2[i] == -1)    // specifies that node is not rechable by any one or both nodes
                continue;
            if(dist1[i] > dist2[i])   // as we need to take max from both dist rechable by both nodes
            {
                if(ans.first > dist1[i])  // as we take minimum among all those max distances
                {
                    ans.first = dist1[i]; // will store minimum dist
                    ans.second = i; // will store resultant node
                }
            }
            else
            {
                if(ans.first > dist2[i])
                {
                    ans.first = dist2[i];
                    ans.second = i;
                }
            }
        }

        if(ans.second == INT_MAX)return -1;  // if we dont have resultant node
        return ans.second;
    }
};
```

