class Solution {
public:

    unordered_map<int,int> dfs(vector<int> edges,int node,unordered_map<int,int>&map,int step){

        //make the distance from first node to itself = 0
        if(step == 1)
            map[node] = 0;

        //itrate over the whole tree
        while(edges[node]!=-1){
            //this line check for loops
            if(map.find(edges[node])!=map.end())
                return map;

            map[edges[node]] = step;
            node = edges[node];
            step++;        
        }
        return map;  
    }

    int closestMeetingNode(vector<int>& edges, int node1, int node2) {
        if(node1 == node2)
            return node1;

        unordered_map<int,int>map1;
        unordered_map<int,int>map2;

        dfs(edges,node1,map1,1);
        dfs(edges,node2,map2,1);

        int z = INT_MAX;
        int number = -1;
        int x=0,y=0;
        for(int i=0;i<edges.size();i++){
            if(map1.find(i)!=map1.end() && map2.find(i)!=map2.end()){
                x = map1[i];
                y = map2[i];
                int r = max(x,y);
                if( z > r ){
                    z = r;
                    number = i;
                }
            }       
        }

        return number; 
    }
};
