class Solution {
public:
    int inf = 1e9+7;
    vector<int> bfs(vector<int> &a, int source){
        queue<int> q;
        int n = a.size();
        vector<int> d(n, inf); // Distance vector to store the distance of each
                               // node from given source node.
        q.push(source);
        int distance = 0; 
        while(!q.empty()){
            int qs = q.size();
            while(qs--){
                int u = q.front();
                q.pop();

                if(d[u] != inf) // To avoid cycles
                    continue;

                d[u] = distance;
                if(a[u] != -1)
                    q.push(a[u]);
            }
            distance++;
        }
        return d;
    }
    int closestMeetingNode(vector<int>& edges, int node1, int node2) {
        int n = edges.size();

        vector<int> d1, d2;
        d1 = bfs(edges, node1);
        d2 = bfs(edges, node2);

        int curr_min = inf, ans = -1;
        for(int i = 0; i < n; i++){
            int node_max = max(d1[i], d2[i]); // maximum distance between ith node to node1
                                              // and ith node to node2.
            if(curr_min > node_max){
                ans = i;
                curr_min = node_max;
            }
        }

        return ans;
    }
};
