class Solution {
public:
    void solve(int s,vector<int>& edges,vector<int>& dist)
    {
        if(edges[s]!=-1){
            if(dist[edges[s]]!=-1) //checks for loops
            return;
            dist[edges[s]]=dist[s]+1;
            solve(edges[s],edges,dist);
        }
    }
    int closestMeetingNode(vector<int>& edges, int node1, int node2) {

        pair<int,int> ans={-1,INT_MAX};
        //first element will represent the answer node
        //second element is max distance that is to be minimized

        int n=edges.size();
        vector<int> dist1(n,-1);
        dist1[node1]=0;
        vector<int> dist2(n,-1);
        dist2[node2]=0;
        solve(node1,edges,dist1);
        solve(node2,edges,dist2);
        for(int i=0;i<n;i++)
        {
            if(dist1[i]!=-1 && dist2[i]!=-1)
            {
                if(ans.second>max(dist1[i],dist2[i]))
                {
                    ans.first=i;
                    ans.second=max(dist1[i],dist2[i]);
                }
            }
        }
        return ans.first;

    }
};
