class Solution {
public:
    vector<int> bfs(const vector<int>& edges,const int& node)
    {
        vector<int> v(edges.size(),-1);
        int x = node , dist = 0;
        v[node] = 0;
        while(true)
        {
            if(edges[x]==-1 || v[edges[x]]!=-1)break;
            v[edges[x]] = ++dist,x = edges[x];
        }
        return v;
    }
    int closestMeetingNode(vector<int>& edges, int node1, int node2) {
        vector<int> v1 = bfs(edges,node1) , v2 = bfs(edges,node2);
        int mn = INT_MAX , ans=-1;
        for(int i = 0 ; i < edges.size() ; i++)
        {
            if(v1[i]==-1 || v2[i]==-1)continue;
            int mx = max(v1[i],v2[i]);
            if( mx < mn ) mn = mx , ans = i;
        }
        return ans;
    }
};
