class Solution {
public:
    int closestMeetingNode(vector<int>& edges, int node1, int node2) {
        vector<int> c1( edges.size(),-1), c2( edges.size(),-1);
        vector<bool> v1(edges.size(),false), v2( edges.size(),false);
        DFS(node1,v1,c1,edges,0);
        DFS(node2,v2,c2,edges,0);

        int ans = edges.size(), res=-1;
        for (int i=0; i < edges.size(); i++){
            if (c1[i] == -1 || c2[i] == -1) continue;
            if (max(c1[i],c2[i]) < ans){
                ans = max(c1[i],c2[i]);
                res =i;
            }
        }
        return res;
    }

    void DFS(int startNode, vector<bool>& v, vector<int>& cv,vector<int>& e, int cost)
    {
        if (startNode < e.size() && startNode != -1 && v[startNode] == false){
            cv[startNode] = cost;
            v[startNode] = true;
            DFS(e[startNode], v, cv, e, cost+1);
        }
    }
};
