    def closestMeetingNode(self, edges: List[int], node1: int, node2: int) -> int:
        num1 = []
        num2 = []

        st = set()

        while node1 not in st:
            if node1 == -1:
                break 
            num1.append(node1)
            st.add(node1)
            node1 = edges[node1]

        st = set()

        while node2 not in st:
            if node2 == -1:
                break
            num2.append(node2)
            st.add(node2)
            node2 = edges[node2]

        st2 = set(num2)
        st1 = set(num1)

        ans1 = -1
        l1 = 0

        for z in num1:
            if z in st2:
                ans1 = z 
                i = num1.index(z)
                j = num2.index(z)
                l1 = max(i,j)
                break 

        ans2 = -1
        l2 = 0

        for z in num2:
            if z in st1:
                ans2 = z 
                i = num1.index(z)
                j = num2.index(z)
                l2 = max(i,j)
                break 

        if ans1 == -1:
            return ans2 
        if ans2 == -1:
            return ans1 

        if l1 < l2:
            return ans1
        if l2 < l1:
            return ans2
        return min(ans1, ans2)

