# /2359. Find Closest Node to Given Two Nodes
# cio
class Solution:
    def closestMeetingNode(self, edges: List[int], node1: int, node2: int) -> int:
        adj = collections.defaultdict(list)
        for i, dst in enumerate(edges):
            adj[i].append(dst)

        def bfs(Node, distMap):
            q = collections.deque()
            q.append([Node,0])
            distMap[Node] = 0
            while q:
                node, dist = q.popleft()
                for nei in adj[node]:
                    if nei not in distMap:
                        q.append([nei, dist + 1])
                        distMap[nei] = dist + 1
        node1Dist, node2Dist = {}, {}
        bfs(node1,node1Dist); bfs(node2,node2Dist)

        print(node1Dist,node2Dist)

        res = -1
        resDist = float("inf")
        for i in range(len(edges)):
            if i in node1Dist and i in node2Dist:
                dist = max(node1Dist[i],node2Dist[i])
                if dist < resDist:
                    res = i
                    resDist = dist

        return res
