class Solution {
public:
    int closestMeetingNode(vector<int>& edges, int node1, int node2) {
        int n=edges.size();
        vector<int> adj[n],in(n,0);
        for(int i=0;i<n;i++){
            if(edges[i]!=-1){
                adj[i].push_back(edges[i]);
                in[edges[i]]++;
            }
        }
        auto dijkstra=[&](int node,vector<int> &dp,vector<int> adj[]){
            dp[node]=0;
            priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>> mn_h;
            mn_h.push({0,node});
            while(!mn_h.empty()){
                auto curr=mn_h.top();mn_h.pop();
                int d=curr.first,u=curr.second;
                if(d>dp[u]) continue;
                for(auto v:adj[u]){
                    if(d+1<dp[v]){
                        dp[v]=1+d;
                        mn_h.push({dp[v],v});
                    }
                }
            }
        };
        vector<int> dp1(n,INT_MAX),dp2(n,INT_MAX);
        dijkstra(node1,dp1,adj);
        dijkstra(node2,dp2,adj);
        int dist=INT_MAX,target_node=-1;
        for(int i=0;i<n;i++){
            int temp=max(dp1[i],dp2[i]);
            if(temp<dist){
                dist=temp;
                target_node=i;
            }
        }
        return target_node;
    }
};
