class Solution {
public:
    int closestMeetingNode(vector<int>& edges, int n1, int n2) {
        int n=edges.size();
        vector<pair<int,int>>st(n,{-1,-1});
        int i=0;
        while(n1!=-1 && st[n1].first==-1){
            st[n1].first=i;
            i++;
