/**
 * @param {number[]} edges
 * @param {number} node1
 * @param {number} node2
 * @return {number}
 */
var closestMeetingNode = function (edges, node1, node2) {
    const distanceFromNode1 = new Array(edges.length);
    const distanceFromNode2 = new Array(edges.length);

    const dfs = (index, count, arr) => {
        arr[index] = count;
        // if current node is not connected to any node, or its next connection has already been visited
        // in case of cycle, then return
        if (edges[index] === -1 || arr[edges[index]] !== undefined) return
        dfs(edges[index], count + 1, arr);
    }
    dfs(node1, 0, distanceFromNode1);
    dfs(node2, 0, distanceFromNode2);

    let min = Infinity, index = -1;
    for (let i = 0; i < distanceFromNode1.length; i++) {
        if (distanceFromNode1[i] !== undefined && distanceFromNode2[i] !== undefined) {
            let currMax = Math.max(distanceFromNode1[i], distanceFromNode2[i])
            if (currMax < min) {
                min = currMax;
                index = i;
            }
        }
    }
    return index;
};
```

