  class Solution:
def closestMeetingNode(self, edges: List[int], node1: int, node2: int) -> int:
    def solution_dict(node):
        res = {}
        flag = 0
        while node!=-1:
            if node in res:
                break
            res[node]=flag
            node = edges[node]
            flag +=1
        return res

    if node1==node2:
        return node1
    dict1, dict2 = solution_dict(node1), solution_dict(node2)
    cands = set(dict1) & set(dict2)
    return min(cands, key=lambda c:(max(dict1[c], dict2[c]), c), default =-1)


