use std::collections::HashSet;

impl Solution {
    pub fn is_valid_sudoku(board: Vec<Vec<char>>) -> bool {
        let mut row_set = HashSet::new();
        let mut col_set = HashSet::new();
        let mut sub_box_sets = vec![HashSet::new(); 9];

        for r in 0..9 {
            for c in 0..9 {
                if let Some(digit) = board[r][c].to_digit(10) {
					// Has this combination of row number r and digit already been encountered?
                    if !row_set.insert((r, digit)) {
                        return false;
                    }
					// Has this combination of column number c and digit already been encountered?
                    if !col_set.insert((c, digit)) {
                        return false;
                    }
					// Iterator math to determine which of the 9 sub boxes the current board[r][c] is in. 
					// Has it already been encountered?
                    if !sub_box_sets[3 * (r/3) + c/3].insert(digit) {
                        return false;
                    }
                }
            }
        }
        true
    }
}
