        if(root == NULL || root == p || root == q)
            return root;

        TreeNode* left = LCA(root->left, p, q);
        TreeNode* right = LCA(root->right, p, q);

        if(left == NULL)
            return right;
        if(right == NULL)
            return left;
        else // both left and right are non NULL
            return root;
    }

    // All nodes exist in the tree
    TreeNode* lowestCommonAncestor(TreeNode* root, vector<TreeNode*> &nodes) {
        int n = nodes.size();
        if(nodes.size() == 1)
            return nodes[0];

        TreeNode* prev = nodes[0];

        // finding LCA of two nodes at a time
        for(int i=1; i<n; i++){
            prev = LCA(root, prev, nodes[i]);
        }
        return prev;
    }
};
