class Solution {

    public List<TreeNode> children(TreeNode node) {
        List<TreeNode> list = new ArrayList<>();
        list.add(node.left);
        list.add(node.right);
        //list.add(node.child3);
        //list.add(node.child4);
        //....
        return list;
    }

    public TreeNode find(TreeNode root, Set<TreeNode> toFind) {

        if (toFind.isEmpty() || root == null) return null;

        boolean matchingNode = false;
        if (toFind.contains(root)) {
            toFind.remove(root);
            if (toFind.isEmpty()) return root;
            matchingNode = true;
        }

        for (TreeNode child: children(root)) {
            int matched = toFind.size();
            TreeNode node = find(child, toFind);
            if (toFind.isEmpty()) {
                if(matchingNode) return root;
                else return node;
            } else if (matched != toFind.size()) {
                matchingNode = true;
            }
        }

        return null;
    }

    public TreeNode lowestCommonAncestor(TreeNode root, TreeNode p, TreeNode q) {
        Set<TreeNode> toFind = new HashSet<>(); 
        toFind.add(p);
        toFind.add(q);
        //toFind.add(r);
        //.....
        return find(root, toFind);
    }

}
