class Solution {
public:
    TreeNode* lowestCommonAncestor(TreeNode* root, TreeNode* p, TreeNode* q) {
        // if root == null or current node is equal to either p or q 
        if(!root || (root->val == p->val) || (root->val==q->val)){
            return root;
        }

        // search in left and right subtree, basically postorder traversal
        TreeNode* left = lowestCommonAncestor(root->left, p, q);
        TreeNode* right = lowestCommonAncestor(root->right, p, q);

        // check condition
        if(left==nullptr && right==nullptr){// if left and right subtree doesn't contain p or q
            return nullptr; 
        }
        if(left && right){// if p and q are present in left and right subtree
            return root;
        }

        // if left subtree contains both p and q return the LCA node returned from left subtree
        // else it is present in right subtree
        return (left!=nullptr)?left:right;

    }
};
```

