class Solution {
    public TreeNode lowestCommonAncestor(TreeNode root, TreeNode p, TreeNode q) {
        // in case root is null (empty tree) return null
        if (root == null) return null;
        // in case current root is either p or q, just return it
        if (root == p || root == q) return root;

        // divide
        TreeNode left = lowestCommonAncestor(root.left, p, q);
        TreeNode right = lowestCommonAncestor(root.right, p, q);

        // in case both left and right are not null, meaning that
        // 1) p exists in the left subtree and q exists in the right subtree
        // 2) q exists in the left subtree and p exists in the left subtree
        // then current root is the LCA in both cases
        if (left != null && right != null) return root;

        // else if only one of left or right is not null
        // which mean that we only find one of p and q in one subtree
        // in that case we are returning it
        if (left != null) return left;
        if (right != null) return right;

        // if left and right are both null, meaning that p, q cannot be found
        // in the current subtree, then we should return null to indicate that
        return null;
    }
}
