var lowestCommonAncestor = function(root, p, q) {
    // deal with the base case, where current node is null
    if (!root) return null;
    // in case current node is either p or q, just return it
    if (root === p || root === q) return root;

    // DIVIDE
    const left = lowestCommonAncestor(root.left, p, q);
    const right = lowestCommonAncestor(root.right, p, q);

    // in case that both left and right are not null, which mean that p and q
    // are located in both left and right subtree, so current node will be the
    // LCA
    if (left && right) return root;

    // else if in case that one of the p and q exists in one of the subtree,
    // then we still return it
    if (left) return left;
    if (right) return right;

    // in case none of p and q exists, return null
    return null;
};
