var deleteNode = function(node) {
    if (!node.next) return;

    // If we reach here, we have at least 2 nodes
    // We stop on the 2nd to last node
    while (node.next.next) {
        node.val = node.next.val;
        node = node.next;
    }
    // Get value of last node and delete last node
    node.val = node.next.val
    node.next = null;
};
```

