class Solution {
public:
    int countHillValley(vector<int>& nums) 
    {
        int ans = 0;                   // for storing the final result
        int k = -1;                    // it is used as a pointer for the new creating nums without duplicaccy
        int size = 0;                  // it will store the size of new nums 

        //eliminating duplicates from the array
        for(int i = 0; i<nums.size(); i++)
        {
            if(k == -1 or nums[k]!=nums[i])       // k==-1 means first element and if there is no duplicaccy then  
            {
                size++;                           // increase the size
                nums[k+1] = nums[i];              // assign the i to k+1
                k++;                              // move pointer to the next
            }
        }

        for(int i = 1; i<size-1; i++)
        {
            // H  ills
            if((nums[i]>nums[i+1] and nums[i]>nums[i-1]))
            {
                ans++;
            }

            // Valleys
            if((nums[i]<nums[i+1] and nums[i]<nums[i-1]))
            {
                ans++;
            }
        }

        return ans;
    }
};
