	Initialize res[] =  [' ', ' ', ' ', ' ', ' ', ' ']

	                  0  1  2  3  4  5
	 nums[]= 	     [2, 4, 1, 1, 6, 5]
	i: 0, res[]:     [ ,  ,  ,  ,  ,  ]
	i: 1, res[]:     [ , H,  ,  ,  ,  ]               // keep updating 'res' array in each iteration of i
	i: 2, res[]:     [ , H, v,  ,  ,  ]
	i: 3, res[]:     [ , H, v, V,  ,  ]
	i: 4, res[]:     [ , H, v, V, H,  ]

	Now, count the number of uppercase H & V (and ignore all lowercase h & v).
	So, return count = 3   (because, H, V, H)

## Code:
	public int countHillValley(int[] nums) {
			int count = 0;
			int len = nums.length;
			char[] res = new char[len];
			Arrays.fill(res,' ');

			for(int i = 1; i < len-1; i++){
				if(nums[i] > nums[i-1]){                                                 // if... num at i > num at i-1                      
					if(nums[i] > nums[i+1]){ res[i] = 'H'; }                                 // also, if num at i > num at i+1. => found Hill. Mark as 'H'   
					else if(nums[i] == nums[i+1]){ res[i] = 'h'; }                           // else, if num at i == num at i+1. Then mark as 'h' instead of 'H'. 
																							    // Means, it may or may not be hill, and will be figured out in next steps.
				}else if( nums[i] < nums[i-1] ){                                         // Else if....
					if(nums[i] < nums[i+1]){ res[i] = 'V'; }                                 //  means, found Valley. So, mark as 'V' 
					else if(nums[i] == nums[i+1]){ res[i] = 'v'; }                              // Means, it may or may not be a valley, and will be figured out in next steps.
				}else{                                                                   // Else.. Here we will figure out if 'v' becomes valley 'V', and if 'h' becomes hill 'H'
					if( res[i-1] == 'v' && nums[i] < nums[i+1] ){ res[i] = 'V'; }     
					else if( res[i-1] == 'h' && nums[i] > nums[i+1] ){ res[i] = 'H'; }
					else{ res[i] = res[i-1]; }
				}
			}        

			for(int i = 1; i < len-1; i++){                                              // Now, count the number of uppercase H & V (and ignore all lowercase h & v).
				if(res[i] == 'H' || res[i] == 'V'){ count++; }   
			}        
			return count;
		}
