    public int countHillValley(int[] nums) {
        int count = 0, prev = 0;

		for(int i=1; i<nums.length-1; i++) {
			if(nums[i] == nums[i+1]){ continue; }                     // if this and next values are equal, we can not yet confirm if there is Hill or Valley.
			else{
				if( (nums[i] > nums[prev] && nums[i] > nums[i+1]) || (nums[i] < nums[prev] && nums[i] < nums[i+1]) ){ 
                    count++; 
                }
				prev = i;  
			}
		}
		return count;
