class Solution {
public:
    int countHillValley(vector<int>& nums) {
        int ans = 0;


        for(int i=1;i<nums.size()-1;i++){

            if(nums[i]==nums[i-1]) continue; 
            int l = nums[i-1];
            int r = nums[i+1];

            if(l==nums[i]){
                int j = i;
                while(j>=0 && nums[j]==l){
                    j--;
                }
                if(j<0) continue;
                if(nums[j]==l) continue;
                else l = nums[j];
            }
            if(r==nums[i]){
                int j = i;
                while(j<nums.size() && nums[j]==r){
                    j++;
