                if(j==nums.size()) continue;
                if(nums[j]==r) continue;
                else r = nums[j];
            }

            if(nums[i]>l && nums[i]>r) ans++;
            else if(l>nums[i] && r>nums[i]) ans++;

        }

        return ans;
    }
};
