// C++
class Solution {
public:
    int countHillValley(vector<int>& nums) {
        int n = nums.size(), prev = nums[0], ans = 0;
        for(int i = 1; i < n; i++) {
            while(i < n && nums[i] == nums[i - 1]) i++;
            if(i < n && ((nums[i - 1] > nums[i] && nums[i - 1] > prev) || (nums[i - 1] < nums[i] && nums[i - 1] < prev))) {
                ans++;
            }
            prev = nums[i - 1];
        }
        return ans;
    }
};


