		int pre = nums[0]; // record the previous num 
		int res = 0;

		for(int i=1; i<nums.length-1; ++i){
			if(nums[i] == nums[i-1]) continue; // find a duplicate, which could be in the same hills or valley as the previous number, we just ignore 

			int cur=nums[i]; // record current num 
			int next=nums[i+1];// record the next num 
			int j=i+1; 

			while(next==cur){ // if next == cur, we have to skip it 
				j++;
				if(j>nums.length-1)break; // if next > nums.length-1, we run out of number; 
				next = nums[j]; // we find a valid number 
				i=j-1; // i should jump to the last duplicate number of cur 
			}
			if((pre<cur && cur>next) || (pre>cur && next>cur)){ // compute hills or valley 
				res++;
			} 
			pre = cur; // relocate pre 
		}
		return res; 
	}
}

// time O(n)
// space O(1)

