class Solution {
    public int countHillValley(int[] nums) {

        int numHillsValleys = 0;  // what we'll return

        int left = nums[0];
        int mid = nums[0];
        int right = nums[0];

        for (int i = 0; i < nums.length; ++i) {

            right = nums[i];

            // case: found valley
            if (left > mid && mid < right) {
                ++numHillsValleys;
                left = mid;
                mid = right;
            }
            // case: on non-increasing slope
            if (left >= mid && mid >= right) {
                mid = right;  // update mid
            }
            // case: found hill
            if (left < mid && mid > right) {
                ++numHillsValleys;
                left = mid;
                mid = right;
            }
            // case: on non-decreasing slope
            if (left <= mid && mid <= right) {
                mid = right;  // update mid
            }
        }

        return numHillsValleys;
    }
}

