class Solution {
    //balance will be 1 if hill and -1 if valley, and count will be updated based on the last balance value
    public int countHillValley(int[] nums) {
        int count = 0;
        int balance = 0;
        for(int i=1; i<nums.length; i++) {
            if(nums[i] > nums[i-1]) {
                if(balance == -1)
                    count++;
                balance = 1;
            }
            if(nums[i] < nums[i-1]) {
                if(balance == 1)
                    count++;
                balance = -1;
            }
        }
        return count;
    }
}
