class Solution {
public:
    int countHillValley(vector<int>& v) {

        int n = v.size();

        int c = 0;

        for(int i=1;i<n-1;i++){
            if(i!=1 && v[i]==v[i-1]) continue;

            int l = i-1 , h = i+1;

            while(l>=0 && v[l]==v[i]) l--;
            while(h<n  && v[h]==v[i]) h++;

            if(l<0 || h>=n) continue;

            if(v[i] > v[l] && v[i] > v[h]) c++;
            if(v[i] < v[l] && v[i] < v[h]) c++;
        }

        return c;

    }
};
