impl Solution {
    pub fn count_hill_valley(nums: Vec<i32>) -> i32 {
        let mut cur_sign;
        let mut count = 0;
        let mut prev_sign = 0;
        for (&x, &y) in nums.iter().zip(nums[1..].iter()).filter(|(x, y)| x != y) {
            cur_sign = (y - x).signum();
            if cur_sign != prev_sign && prev_sign != 0 {
                count += 1;
            }
            prev_sign = cur_sign;
        }
        count
    }
}
