class Solution {
  public int countHillValley(int[] nums) {
    List<Integer> uniqueVals = new ArrayList<>();
    for (int n : nums) {
      if (uniqueVals.isEmpty() || uniqueVals.get(uniqueVals.size() - 1) != n) {
        uniqueVals.add(n);
      }
    }
    int count = 0;
    for (int i = 1; i < uniqueVals.size() - 1; i++) {
      if ((uniqueVals.get(i) > uniqueVals.get(i - 1))
          == (uniqueVals.get(i) > uniqueVals.get(i + 1))) {
        count++;
      }
    }
    return count;
  }
}
