class Solution {
    public int countHillValley(int[] nums) {

        int count=0;

        for ( int i=1 ; i < nums.length-1 ; i++)  
        {
            // 1. find  hill 
            // left neighbor smaller
            if(nums[i-1] < nums[i] ) 
            {
                // skip all equal number 
                 while(  i < nums.length-1 && (nums[i] == nums[i+1] ))
                     i++;

                 // if we get right closest neighbor is smaller , this is hill
                 if ( i < nums.length-1 && nums[i+1] < nums[i])
                   count++;
            }   
             // 1. find  Valley
              // left neighbor greater
            else if(nums[i-1] > nums[i] ) 
            {
                // skip all equal number 
                 while(i < nums.length-1 && (nums[i] == nums[i+1] ))
                     i++;
                 // if we get right closest neighbor is greater , this is Valley
               if ( i < nums.length-1 && nums[i+1] > nums[i])
                   count++;
            } 
        }

        return count;

    }
}
