class Solution:
    def countHillValley(self, nums: List[int]) -> int:
        r = []
        i = 1
        while i < len(nums)-1:
            temp = nums[:i][::-1] 
            for j in temp:                
                if (nums[i] != j ) :
                    break
            for k in nums[i+1:] :
                if (nums[i] != k) :
                    break
            if (nums[i] > j and nums[i] > k) or (nums[i] < j and nums[i] < k) :
                l = [j, k]
                r += [l]
            i += 1

        if r:
            c = 0
            for i in range(len(r)-1) :
                if r[i] != r[i+1] :
                    c += 1
            return c+1
        return 0

