var countHillValley = function(nums) {
    let cnt = 0;
    const u =  []

    for (const n of nums) {
        if (u.length === 0 || u.at(-1) !== n) {
            u.push(n)
        }
    }

    for (let i = 1; i<u.length-1; i++) {
        if (u[i] > u[i-1] && u[i] > u[i+1] ||
        u[i] < u[i-1] && u[i] < u[i+1]) {
            cnt++;
        }
    }

    return cnt;
};
