class Solution {
    public int countHillValley(int[] nums) {
        int ans = 0;
        for(int i=1 ; i<nums.length-1; i++){
            if(nums[i]==nums[i-1]) {continue;}
            int left = closestLeft(i,nums);
            int rigt = closestRigt(i,nums);
            if(left>nums[i] && rigt>nums[i]) ans++;
            if(left<nums[i] && rigt<nums[i]) ans++;
        }
        return ans;
    }
    private int closestLeft(int index, int[] arr) {
        if(arr[index-1]!=arr[index]) return arr[index-1];
        else {
            int i = index-1;
            while(i>0){
                if(arr[i]!=arr[index]) return arr[i];
                else i--;
            }
            return arr[index];
        }
    }
    private int closestRigt(int index, int[] arr) {
        if(arr[index+1]!=arr[index]) return arr[index+1];
        else {
            int i = index+1;
            while(i<arr.length){
                if(arr[i]!=arr[index]) return arr[i];
                else i++;
            }
            return arr[index];
        }
    }
}
```

