object Solution {
    def countHillValley(list: Array[Int]): Int = {
    var i = 0
      var count: Int = 0
      while (i < list.length - 1) {
        val leftNonEqual = (i - 1 to 0 by -1).collectFirst { case x if list(x) != list(i) => x }
        val rightNonEqual = (i + 1 until list.length).collectFirst { case x if list(x) != list(i) => x }

        val isHillOrValley = for {
          left <- leftNonEqual
          right <- rightNonEqual
        } yield (list(left) < list(i) && list(i) > list(right)) || (list(left) > list(i) && list(i) < list(right))

        i = rightNonEqual.getOrElse(i + 1)
        count += (isHillOrValley match {
          case Some(true) => 1
          case _ => 0
        })
      }

      count
    }
}
```

