class Solution {
public:
    // last_pos = 0  --> Initial position
    // last_pos = 1  --> At the Hill.
    // last_pos = 2  --> At the Valley.

    #define START   0
    #define HILL    1
    #define VALLEY  2
    int countHillValley(vector<int>& nums) {
        int count = 0, last_pos = START;
        for (int i = 1; i < nums.size(); i++) {
            if (nums[i] > nums[i - 1]) {
                count += (last_pos == VALLEY ? 1 : 0);
                last_pos = HILL;
            } else if (nums[i] < nums[i - 1]) {
                count += (last_pos == HILL ? 1 : 0);
                last_pos = VALLEY;
            }
       }
       return count;
    }
};
