class Solution {
public:
    int countHillValley(vector<int>& nums) {
        int ct=0,n=nums.size();
        vector<int> dp1(n,-1),dp2(n,-1);
        for(int i=1;i<n;i++){
            if(nums[i]==nums[i-1]) dp1[i]=dp1[i-1];
            else dp1[i]=nums[i-1];
        }
        for(int i=n-2;i>=0;i--){
            if(nums[i]==nums[i+1]) dp2[i]=dp2[i+1];
            else dp2[i]=nums[i+1];
        }
        for(int i=1;i<n-1;i++){
            if(dp1[i]!=-1 && dp2[i]!=-1 && nums[i]>dp1[i] && nums[i]>dp2[i] && nums[i]!=nums[i-1]) ct++;
            if(dp1[i]!=-1 && dp2[i]!=-1 && nums[i]<dp1[i] && nums[i]<dp2[i] && nums[i]!=nums[i-1]) ct++;
        }
        return ct;
    }
};
