class Solution {
public:
    int countHillValley(vector<int>& nums) {
        int ans = 0;

        for (int i=1 ; i<nums.size()-1 ; i++){
            int left=-1 , right=-1;
            int j=i-1 ; 
            while (j>=0){
                if (nums[j] != nums[i]){
                    left = j;
                    break;
                }
                j--;
            }

            j = i+1;
            while (j<nums.size()){
                if (nums[j] != nums[i]){
                    right = j;
                    break;
                }
                j++;
            }
            if (left == -1 || right == -1)
                continue;

            if (nums[i]>max(nums[left] , nums[right]) || nums[i]<min(nums[left] , nums[right])){    
                if (nums[i] != nums[i-1]) {
                    ans++;
                }
            }
        }    
        return ans;
    }
};
