class Solution {
    public int countHillValley(int[] nums) {
        int count = 0;
        for(int i = 1, j = 0, k = 2; i < nums.length - 1; i++, j++, k++) {
            boolean isTheSameHillOrValley = i > 1 && nums[i] == nums[i - 1];
            if (!isTheSameHillOrValley) {
                int l = nums[j], m = nums[i], r = nums[k];
                if (l == m) {
                    int index = j;
                    while (index > 0 && l == m) {
                        l = nums[--index];
                    }
                }
                if (r == m) {
                    int index = k;
                    while (index + 1 < nums.length && r == m) {
                        r = nums[++index];
                    }
                }
                if ((m > l && m > r) || (m < l && m < r)) {
                    count++;
                }
            }
        }
        return count;       
    }
